const items = $input.all().map(item => item.json);

// Hàm chuyển đổi mảng thành HTML và loại bỏ "```html"
function arrayToHtml(array) {
    let html = '';

    array.forEach(item => {
        let content = item.para || '';
        // Loại bỏ "```html" ở đầu nếu có
        if (content.startsWith('```html')) {
            content = content.substring('```html'.length).trimStart();
        }
        // Loại bỏ "```" ở đầu nếu có (trường hợp chỉ có ```)
        else if (content.startsWith('```')) {
            content = content.substring('```'.length).trimStart();
        }

        // Loại bỏ "```" hoặc "```html" ở cuối nếu có
        if (content.endsWith('```html')) {
            content = content.substring(0, content.length - '```html'.length).trimEnd();
        } else if (content.endsWith('```')) {
            content = content.substring(0, content.length - '```'.length).trimEnd();
        }

        // Thêm đoạn văn bản
        html += '<div class="content-section">';
        const paragraphs = content.split('\n\n').map(p => `<p>${p}</p>`).join('');
        html += paragraphs;
        html += '</div>';

        // Thêm HTML ảnh từ images_para nếu có
        if (item.images_para) {
            html += '<div class="image-section">';
            html += item.images_para; // Không thêm thẻ img mới, giữ nguyên như đã có
            html += '</div>';
        }
    });

    return html;
}

// Gọi hàm và trả về kết quả
const htmlContent = arrayToHtml(items);

return [{
    json: {
        html_content: htmlContent
    }
}];

