// --- Config ---
const count = $('paragraphs').all().length; // Giới hạn số ảnh đầu ra

const allItems = $input.all();
let output = [];
let counter = 1;
const timestamp = new Date().toISOString().replace(/[:.]/g, "-");

const isImageLink = (url) => {
  return /\.(jpg|jpeg|png|webp|gif)(\?.*)?$/i.test(url);
};

const getFileName = (url) => {
  try {
    return url.split('/').pop().split('?')[0]; // bỏ query
  } catch (e) {
    return '';
  }
};

const seenFilenames = new Set(); // Để tránh ảnh trùng tên (URL giống nhau)

for (const item of allItems) {
  const results = item.json.items;

  if (results && Array.isArray(results)) {
    for (const img of results) {
      if (output.length >= count) break; // Ngừng nếu đã đủ ảnh

      const url = img.link;
      if (!isImageLink(url)) continue;

      const filenameRaw = getFileName(url);
      if (seenFilenames.has(filenameRaw)) continue; // Bỏ nếu đã thấy ảnh trùng tên

      seenFilenames.add(filenameRaw);

      const ext = filenameRaw.split('.').pop().split('?')[0].toLowerCase();
      const contentType = `image/${ext === 'jpg' ? 'jpeg' : ext}`;

      const filename = `image-${timestamp}-${counter}.${ext}`;

      output.push({
        json: {
          images_para: url,
          filename: filename,
          contentType: contentType
        }
      });
      counter++;
    }
  }

  if (output.length >= count) break; // Ngừng duyệt input nếu đã đủ ảnh
}

return output;

